#!/bin/bash

# Create simple placeholder icons for the extension
# These are base64 encoded 1x1 pixel PNGs

# 16x16 icon (microphone emoji as placeholder)
echo "" | base64 -d > icon16.png

# 48x48 icon
echo "" | base64 -d > icon48.png

# 128x128 icon
echo "" | base64 -d > icon128.png

echo "Icons created successfully!"
